<?php if(!empty($post)):	$clapat 	= get_post_meta($post->ID,'clapat_advanced', true);	$details 	= array();	if($clapat != null){		$details = (array) json_decode($clapat);	}		$photos = (isset($details['photos']))? $details['photos'] : array();	$gallery = '';	if(count($photos) > 0){		foreach($photos as $photo){			if(trim($photo) == '') continue;			$gallery .= '<li> <img src="'.trim($photo).'" /> </li>';		}	}		if($gallery != ''):		?>		<div class="flexslider blog clearfix">			<ul class="slides">				<?php echo $gallery; ?>			</ul>  		</div>		<?php endif; endif; ?>