<?php if(!empty($post)):?><div class="postquote wrap"><div class="post quote clearfix" onClick="document.location.href='<?php the_permalink();?>'">	<div class="clearfix">		<h2 class="title"><a href="<?php the_permalink();?>"><?php the_content(); ?></a></h2>		<div class="pcontent"><i class="icon-quote-right quot"></i><?php the_title(); ?></div>		<div class="meta clearfix">			<a href="<?php the_author_meta('user_url', $post->post_author); ?>"><p class="post-admin">Posted by <?php the_author(); ?></p></a>			<a href="javascript:void(0)"><p class="post-calendar"><?php echo date('d F y', strtotime($post->post_date)); ?></p></a>			<a href="javascript:void(0)"><p class="post-tagss"><?php $tags = get_the_tag_list('',', ',''); echo (!empty($tags))? strip_tags($tags) : __('No Tags','ionuts');?></p></a>			<a href="#comments"><p class="post-commentss "><?php echo get_comments_number(); ?> <?php _e('Comments');?></p></a>		</div>	</div></div></div><?php endif; ?>