<?php $class="";if(!empty($post)):	$clapat 	= get_post_meta($post->ID,'clapat_advanced', true);	$details 	= array();	if($clapat != null){		$details = (array) json_decode($clapat);	}		$video 		= (isset($details['video']))? $details['video'] : '';	if($video != ''):			if(substr_count($video, 'vimeo.com') == 1){				$videoURL = str_replace('http://vimeo.com/','http://player.vimeo.com/video/', $video);			}else if(substr_count($video, 'youtu.be') == 1 ||substr_count($video, 'youtube.com') == 1){				$videoURL = str_replace('/watch?v=','/embed/', $video);	}?>		<div class="video-wrapper">		<div class="video-container">			<iframe width="660" height="330" data-ratio="2" src="<?php echo $videoURL; ?>" frameborder="0" allowfullscreen></iframe>		</div></div>		<?php endif; ?><?php endif; ?>