<?php /*Template name: Blog home*/get_header(); global $clapat_settings, $clapat_design;do_action('alter_settings');$data = array(	'items'	=>	array(		'id'	=> $SiteSections->id,		'name'	=> $SiteSections->name,		'type'	=> $SiteSections->layout,		'ct'	=> $SiteSections->content,	),	'logo' =>	array(		'src' 	=>	((isset($clapat_settings->clapat_logo) && strlen($clapat_settings->clapat_logo) > 5)? $clapat_settings->clapat_logo : ''),		'text'	=>	'',		'url'	=>	home_url(),	),	'nav' => array(		'bg' 	=> @$clapat_design->bg->nav,	),	'home' => array(		'disabled' 	=> ((isset($clapat_settings->clapat_disable_home) && $clapat_settings->clapat_disable_home == 1)? true : false),		'content'	=> $clapat_settings->site_slider,		'id'	=> 'home',		'name'	=> __('Home','clapat'),	),);echo ClapatFrontend::makeNavMenu($data, 1);?><div class="container clearfix blog">	<div class="content left">	<?php 		global $paged;		query_posts('numberposts=-1&post_type=post&paged='.$paged);		if(have_posts()){		while(have_posts()){			the_post();			if(get_post_format() != 'quote'){				get_template_part( 'loop-blog-post-header');			}			get_template_part( 'loop-blog', get_post_format() );				if(get_post_format() != 'quote'){					?>		<div class="pcontent"><?php the_excerpt(); ?></div>		<a href="<?php the_permalink();?>" class="clapat-button"><?php _e('Read more','clapat'); ?></a>					</div></div>		<?php			}		}		get_template_part( 'loop-blog', 'navigation' );		wp_reset_query();			}else{			get_template_part('loop','blog-notfound');		}	?>	</div>	<div class="sidebar right">		<?php get_sidebar();?>	</div></div><?php get_footer(); ?>